USE FIAP;

CREATE TABLE DDD (Id int IDENTITY(1,1) PRIMARY KEY , NumDDD int, regiao varchar(50))

CREATE TABLE Contatos (ID int IDENTITY(1,1) PRIMARY KEY, Nome varchar(100), Telefone varchar(20), Email varchar(50), DDDID int NOT NULL, CONSTRAINT FK_DDDID FOREIGN KEY (DDDID) REFERENCES DDD (Id))

